$(document).ready(function(){"use strict";let externalLinks=function(){let internalHost=[location.hostname];let protocolPattern=/^https?:\/\//i;$("a").each(function(){let href=$(this).attr("href");if(protocolPattern.test(href)){let hrefHostname=$(new URL(href)).attr("hostname");if($.inArray(hrefHostname,internalHost)===-1){$(this).attr("target","_blank");$(this).attr("rel","noopener noreferer")}}})};let checkTime=function(i){if(i<10){i="0"+i}return i};let renderClock=function(element,utcOffset){let today=new Date;let h=today.getUTCHours();let m=today.getUTCMinutes();let s=today.getUTCSeconds();h=h+utcOffset;if(h>24){h=h-24}if(h<0){h=h+24}h=checkTime(h);m=checkTime(m);s=checkTime(s);element.html(h+":"+m+":"+s);setTimeout(function(){renderClock(element,0)},500)};let init=function(){externalLinks();renderClock($(".eve-time-wrapper .eve-time-clock"),0)};init();$(document).ajaxSuccess(function(){externalLinks()})});